// inject_ad.js

console.log('inject_ad.js script has been loaded');

function injectAd() {
  console.log('injectAd function called');

    // Check if the ad is already injected to avoid duplication
    if (document.querySelector('#adContainer')) {
      console.log('Ad is already injected');
      return;
    }
  

  // Create a new div element for the ad
  const adContainer = document.createElement('div');
  adContainer.id = 'adContainer';
  adContainer.style.position = 'fixed';
  adContainer.style.top = '50%';
  adContainer.style.right = '0';
  adContainer.style.transform = 'translateY(-50%)';
  adContainer.style.width = '300px';
  adContainer.style.backgroundColor = '#f1f1f1';
  adContainer.style.borderLeft = '1px solid #ccc';
  adContainer.style.boxSizing = 'border-box';
  adContainer.style.padding = '10px';
  adContainer.style.zIndex = '10000';
  adContainer.style.textAlign = 'center';
  // const imageUrl = browser.runtime.getURL('images/cat_meme1.gif');
  const base64Image = 'data:image/jpg;base64,';
  adContainer.innerHTML = `
  <button id="closeAd" style="position: absolute; top: 10px; right: 10px;">X</button>
  <h3>Sponsored Ad</h3>
  <p id="adText" style="cursor: pointer;">You should try this!</p>
  <img id="adImage" src="${base64Image}" alt="Ad" style="width: 100%; height: auto; cursor: pointer;">
`;

  document.body.appendChild(adContainer);
  console.log('Ad container injected into the page');

  // Add event listener for the close button
  document.getElementById('closeAd').addEventListener('click', () => {
    adContainer.remove();
  });

  // Add event listener for the ad text
  document.getElementById('adText').addEventListener('click', () => {
    window.location.href = 'https://www.bilibili.com/video/BV1WL41177q8/?spm_id_from=333.337.search-card.all.click&vd_source=9f954f15fab083befbe9bf151552742d'; // Replace with your target URL
  });

  // Add event listener for the ad image
  document.getElementById('adImage').addEventListener('click', () => {
    window.location.href = 'https://www.bilibili.com/video/BV1WL41177q8/?spm_id_from=333.337.search-card.all.click&vd_source=9f954f15fab083befbe9bf151552742d'; // Replace with your target URL
  });
}

// Run the injectAd function when the page loads
window.addEventListener('load', injectAd);
console.log('Event listener added for page load');
